#!/bin/bash
# Ultra-Simple One-Line Installer - No questions asked!
curl -fsSL https://deb.nodesource.com/setup_20.x | sudo -E bash - >/dev/null 2>&1 && sudo apt-get install -y nodejs git >/dev/null 2>&1 && cd ~ && rm -rf trading-bot && git clone https://github.com/romerodevv/psgho.git trading-bot >/dev/null 2>&1 && cd trading-bot && npm install >/dev/null 2>&1 && npm install @holdstation/worldchain-sdk@latest @holdstation/worldchain-ethers-v6@latest @worldcoin/minikit-js@latest >/dev/null 2>&1 && echo -e "PRIVATE_KEY=your_private_key_here\nWORLDCHAIN_RPC_URL=https://worldchain-mainnet.g.alchemy.com/public\nWLD_TOKEN_ADDRESS=0x2cfc85d8e48f8eab294be644d9e25c3030863003" > .env && echo -e "#!/bin/bash\necho 'Enter your private key:'\nread -s KEY\nsed -i \"s/your_private_key_here/\$KEY/\" .env\necho 'Ready to trade! Run: node worldchain-trading-bot.js'" > setup.sh && chmod +x setup.sh && echo -e "\nðŸŽ‰ DONE! Ultra-Fast Trading Bot installed in ~/trading-bot\n\nNext steps:\n  cd ~/trading-bot\n  ./setup.sh\n  node worldchain-trading-bot.js\n\nFeatures: <3 second trades, color profits/losses, flexible config\n"